tinymce.PluginManager.add("clear_format", function (editor) {
  "use strict";
  editor.addButton("clear_format", {
    title: "Clear Format",
    icon: false,
    image:
      "data:image/svg+xml;base64,",
    cmd: "RemoveFormat",
  });
});
